var wrapPromise = require('WrapPromise');

class SlackPromiser {

    constructor(slack, token) {
        this.slack = slack;
        this.token = token;
    }

    listChannels() {
        var self = this;
        return wrapPromise(self.slack.channels.list, {'token':self.token});
    }
}

module.exports = SlackPromiser;
